        public static function Calc(Poolorder $poolorder)
        {
            $ret = (doubleval($poolorder->Pool->Price) * $poolorder->Quantity);
            return $ret;
        }


        public static function CalculateTaxes(Poolorder $poolorder, $compound=false)
        {
            if($compound)
            {
                $ret = doubleval($poolorder->Pool->Tax);
            }
            else
            {
                $ret = (doubleval($poolorder->Pool->Tax) * $poolorder->Quantity);
            }
            return $ret;
